INSERT INTO `pastes` VALUES (1,'This is not a paste.','893099a3acc4dfb1a742',NULL,'2009-10-14 15:50:20','2009-10-14 15:50:20'),(2,'oh\naie','7cda79547507efe33282',NULL,'2009-10-15 00:27:15','2009-10-15 00:27:15'),(3,'oh\naie','0f43f922ee6db429c5c4',NULL,'2009-10-15 00:28:35','2009-10-15 00:28:35'),(4,'oh\naie','5b3991994068928f6a65',NULL,'2009-10-15 00:29:13','2009-10-15 00:29:13'),(5,'require \"em-http\"\n\nmodule Talker\n  class Paster\n    PASTE_URL = \"http://talkerapp.com/pastes.json\"\n    PREVIEW_LINES = 15\n    \n    def initialize(token)\n      @token = token\n    end\n    \n    def post(options)\n      EM::HttpRequest.new(PASTE_URL).post(options)\n    end\n    \n    def paste(content, ','d9e2522dcb44de9ee062',NULL,'2009-10-15 00:36:32','2009-10-15 00:36:32'),(6,'oh\naie','55b7713117251d792116',NULL,'2009-10-15 13:40:32','2009-10-15 13:40:32'),(7,'#!/usr/bin/env bash\nserver/bin/talker --paste-url http://localhost:3000/pastes.json \"$@\"\n','785247dfe50bf06fff26',NULL,'2009-10-15 13:46:45','2009-10-15 13:46:45'),(8,'$(function() {\n  $(\"#msgbox\")\n    .keydown(function(e) {\n      if (e.which == 13) {\n        if (this.value == \'\'){ return false }\n        ChatRoom.send(this.value, true)','258b6ecefebefccc7261',NULL,'2009-10-15 15:52:51','2009-10-15 15:52:51'),(9,'$(function() {\n  $(\"#msgbox\")\n    .keydown(function(e) {\n      if (e.which == 13) {\n        if (this.value == \'\'){ return false }\n        ChatRoom.send(this.value, true)','ea23f5646b44938547fa',NULL,'2009-10-15 15:53:18','2009-10-15 15:53:18'),(10,'  this.setHeader = function(header) {\n    this.header = $(\"<div/>\").addClass(\"header\").html(header)','77ad4db77974852db36e',NULL,'2009-10-15 15:55:32','2009-10-15 15:55:32'),(11,'  newMessage: function() {\n    if (this.currentMessage) this.currentMessage.createElement().insertBefore($(\"#message\"))','6ccfa7567c21655df9db',NULL,'2009-10-15 16:18:19','2009-10-15 16:18:19'),(12,'  formatPaste: function(paste) {\n    var msg = \"<a href=\'\"   window.location.protocol   \"//\"   window.location.host   \"/pastes/\"   paste.id   \"\'>View paste</a>\"','bae1504d519fd180cdb0',NULL,'2009-10-15 16:27:14','2009-10-15 16:27:14'),(13,'  formatPaste: function(paste) {\n    var msg = \"<a href=\'\"   window.location.protocol   \"//\"   window.location.host   \"/pastes/\"   paste.id   \"\'>View paste</a>\"','5f20ec9f16fa58120096',NULL,'2009-10-15 16:27:21','2009-10-15 16:27:21'),(14,'var FormatHelper = {\n  text2preview: function(content){\n    return content.replace(/[a-z]/g, \'x\')\n                  .replace(/[A-Z]/g, \'X\')\n                  .replace(/[0-9]/g, \'#\')','54d3f8aee174e36278dd',NULL,'2009-10-15 16:27:27','2009-10-15 16:27:27'),(15,'var FormatHelper = {\n  text2preview: function(content){\n    return content.replace(/[a-z]/g, \'x\')\n                  .replace(/[A-Z]/g, \'X\')\n                  .replace(/[0-9]/g, \'#\')','df50acd91ef1ce0b42ef',NULL,'2009-10-15 16:27:40','2009-10-15 16:27:40'),(16,'var FormatHelper = {\n  text2preview: function(content){\n    return content.replace(/[a-z]/g, \'x\')\n                  .replace(/[A-Z]/g, \'X\')\n                  .replace(/[0-9]/g, \'#\')','8074c2c5a291fa89ba9e',NULL,'2009-10-15 16:30:06','2009-10-15 16:30:06'),(17,'var FormatHelper = {\n  text2preview: function(content){\n    return content.replace(/[a-z]/g, \'x\')\n                  .replace(/[A-Z]/g, \'X\')\n                  .replace(/[0-9]/g, \'#\')','cd1675705b9668dc370a',NULL,'2009-10-15 16:33:04','2009-10-15 16:33:04'),(18,'require \"em-http\"\n\nmodule Talker\n  class Paster\n    PREVIEW_LINES = 15\n    \n    attr_reader :server_url\n    \n    def initialize(server_url)\n      @server_url = server_url\n    end\n    \n    def post(options)\n      EM::HttpRequest.new(@server_url).post(options)\n    end\n    \n    def paste(token, content, ','2b8f87efc577fbdba076',NULL,'2009-10-15 16:35:46','2009-10-15 16:35:46'),(19,'var FormatHelper = {\n  text2preview: function(content){\n    return content.replace(/[a-z]/g, \'x\')\n                  .replace(/[A-Z]/g, \'X\')\n                  .replace(/[0-9]/g, \'#\')','039fedae5862a65f0aad',NULL,'2009-10-15 16:38:08','2009-10-15 16:38:08'),(20,'var FormatHelper = {\n  text2preview: function(content){\n    return content.replace(/[a-z]/g, \'x\')\n                  .replace(/[A-Z]/g, \'X\')\n                  .replace(/[0-9]/g, \'#\');\n  },\n  text2html: function(content, noScroll){\n    var image_expression     = /(^https?:\\/\\/[^\\s]+\\.(?:gif|png|jpeg|jpg)$)/gi;\n    var youtube_expression   = /^(?:http\\S+youtube\\.com\\/watch\\?v=)([\\w-]+)(?:\\S*)$/;\n    var vimeo_expression     = /^(?:http\\S+vimeo\\.com\\/)(\\d+)/;\n    var url_expression       = /[^\\s]+\\.[^\\s|\\.]+/gim;\n    var protocol_expression  = /^(http|https|ftp|ftps|ssh|irc|mms|file|about|mailto|xmpp):\\/\\//;\n    var multiline_expression = /\\n/gim;\n    \n    // forces multiline from being enclosed in <pre> twice and mucking up display.\n    if (content.match(/<!-- ignore -->/g)){\n      return content;\n    } else {\n      var content = content.replace(\'<\', \'&lt;\').replace(\'>\', \'&gt;\');\n    }\n    \n    if (content.match(multiline_expression)){\n      // setup content message inside a div or something with overflow scroll/auto\n      return  \'<!-- ignore --><pre style=\"display: block; margin:\' \n            + \' 1em; font-family: \\\'Lucida Console\\\', monospace; overflow: scroll; width: 70%; background: white\">\' \n            + content \n            + \'</pre>\';\n      // add a link to a message display that would show the message in raw text\n    } else if (content.match(image_expression)){\n      return content.replace(image_expression, function(locator){\n        return \'<a href=\"\' \n          + locator \n          + \'\" target=\"_blank\"><img src=\"\' \n          + locator \n          + \'\" onload=\"ChatRoom.resizeImage(this, \' + (!!noScroll) + \')\" style=\"visibility: hidden;\" />\'\n          + \'</a>\';\n      });\n    } else if (content.match(youtube_expression)){\n      return content.replace(youtube_expression, function(locator){\n        return locator.replace(youtube_expression, \'<object width=\"425\" height=\"355\">\'\n          + \'<param name=\"movie\" value=\"http://www.youtube.com/v/$1?rel=1&color1=0x2b405b&color2=0x6b8ab6&border=0&fs=1\"></param>\'\n          + \'<param name=\"allowFullScreen\" value=\"true\"></param>\'\n          + \'<embed src=\"http://www.youtube.com/v/$1?rel=1&color1=0x2b405b&color2=0x6b8ab6&border=1&fs=1\"\'\n          + \' type=\"application/x-shockwave-flash\"\'\n          + \'  width=\"425\" height=\"355\" \'\n          + \'  allowfullscreen=\"true\"></embed>\'\n          + \'</object>\');\n      });\n    } else if (content.match(vimeo_expression)){\n      return content.replace(vimeo_expression, function(locator){\n        return locator.replace(vimeo_expression, \'<object width=\"400\" height=\"220\">\'\n          + \'<param name=\"allowfullscreen\" value=\"true\" />\'\n          + \'<param name=\"allowscriptaccess\" value=\"always\" />\'\n          + \'<param name=\"movie\" value=\"http://vimeo.com/moogaloop.swf?clip_id=$1\'\n          + \'&amp;server=vimeo.com&amp;show_title=1&amp;show_byline=1&amp;show_portrait=0&amp;color=&amp;fullscreen=1\" />\'\n          + \'<embed src=\"http://vimeo.com/moogaloop.swf?clip_id=$1&amp;server=vimeo.com&amp;show_title=1&amp;show_byline=1&amp;\'\n          + \'show_portrait=0&amp;color=&amp;fullscreen=1\" \'\n          + \'type=\"application/x-shockwave-flash\" allowfullscreen=\"true\" allowscriptaccess=\"always\" width=\"400\" height=\"220\">\'\n          + \'</embed></object>\'\n        );\n      });\n    } else if (content.match(url_expression)) {\n      return content.replace(url_expression, function(locator){\n        return \'<a href=\"\' \n          +  (!locator.match(protocol_expression) ? \'http://\' : \'\') + locator\n          + \'\" target=\"_blank\">\' \n          +   locator \n          + \"</a>\";\n      });\n    } else {\n      return content;\n    }\n  },\n  \n  formatPaste: function(paste) {\n    var msg = \"<a target=\'_blank\' title=\'Paste #\" + paste.id +\n              \"\' href=\'\" + window.location.protocol + \"//\" + window.location.host + \"/pastes/\" + paste.id +\n              \"\'>View paste</a>\";\n    if (paste.lines > paste.preview_lines) msg += \" <span class=\'more_lines\'>(\" + (paste.lines - paste.preview_lines) + \" more lines)</span>\"\n    return msg;\n  }\n}','fae7cdabc442bdea8daa',NULL,'2009-10-15 16:44:41','2009-10-15 16:44:41'),(21,'module Talker\n  module Logger\n    class Room\n      attr_reader :name\n      \n      def initialize(name, server)\n        @name = name\n        @server = server\n        reset\n      end\n      \n      def reset\n        @parser = Yajl::Parser.new\n        @parser.on_parse_complete = method(:received)\n      end\n      \n      def db\n        @server.db\n      end\n      \n      def parse(data)\n        @parser << data\n      rescue Yajl::ParseError => e\n        Talker.logger.error \"Ignoring invalid JSON (room##{@name}): \" + e.message\n        reset\n      end\n      \n      def callback(&callback)\n        @callback = callback\n      end\n      \n      def received(message)\n        type = message[\"type\"]\n        \n        # Shortcircuit if partial message\n        # TODO update existing message if partial?\n        return if type == \"message\" && !message[\"final\"]\n        \n        unless message.key?(\"user\")\n          Talker.logger.error \"No user key in message: \" + message.inspect\n          return\n        end\n        \n        Talker.logger.debug{\"room##{@name}> \" + message.inspect}\n        \n        case type\n        when \"message\"\n          if message[\"paste\"]\n            insert_paste message\n          else\n            insert_message message\n          end\n        when \"join\", \"leave\"\n          insert_notice message\n        else\n          @callback.call\n        end\n      end\n      \n      private\n        def insert_message(message)\n          room_id = @name.to_i\n          user_id = message[\"user\"][\"id\"].to_i\n          uuid = message[\"id\"]\n          content = message[\"content\"]\n          time = message[\"time\"] || Time.now.to_i\n          \n          sql = \"INSERT INTO events (room_id, user_id, type, uuid, message, created_at) \" +\n                \"VALUES (#{room_id}, #{user_id}, \'message\', \'#{quote(uuid)}\', \'#{quote(content)}\', FROM_UNIXTIME(#{time}))\"\n\n          db.insert sql, @callback, errback_for(message)\n        end\n      \n        def insert_paste(message)\n          room_id = @name.to_i\n          user_id = message[\"user\"][\"id\"].to_i\n          uuid = message[\"id\"]\n          content = message[\"content\"]\n          paste_id = message[\"paste\"][\"id\"]\n          time = message[\"time\"] || Time.now.to_i\n          \n          find_paste_id_sql = \"SELECT id FROM pastes WHERE permalink = \'#{quote(paste_id)}\'\"\n          \n          sql = \"INSERT INTO events (room_id, user_id, type, uuid, message, created_at) \" +\n                \"VALUES (#{room_id}, #{user_id}, \'message\', \'#{quote(uuid)}\', \'#{quote(content)}\', (#{find_paste_id_sql}), FROM_UNIXTIME(#{time}))\"\n          \n          db.insert sql, @callback, errback_for(message)\n        end\n      \n        def insert_notice(message)\n          room_id = @name.to_i\n          user_id = message[\"user\"][\"id\"].to_i\n          type = message[\"type\"]\n          time = message[\"time\"] || Time.now.to_i\n          \n          sql = \"INSERT INTO events (room_id, user_id, type, created_at) \" +\n                \"VALUES (#{room_id}, #{user_id}, \'#{quote(type)}\', FROM_UNIXTIME(#{time}))\"\n          \n          db.insert sql, @callback, errback_for(message)\n        end\n        \n        def errback_for(message)\n          proc { |e| Talker.logger.error \"Error logging message: #{message.inspect}, error: #{e}\" }\n        end\n      \n        def quote(s)\n          return \"\" if s.nil?\n          s.gsub(/\\\\/, \'\\&\\&\').gsub(/\'/, \"\'\'\")\n        end\n    end\n  end\nend','6a4021af7f0e77c87142',NULL,'2009-10-15 18:44:23','2009-10-15 18:44:23'),(22,'require File.dirname(__FILE__) + \"/spec_helper\"\n\nEM.describe Talker::Logger do\n  before do\n    @logger = Talker::Logger::Server.new :database => \"talker_test\", :user => \"root\"\n    @logger.start\n    \n    @exchange = MQ.fanout(\"talker.test\")\n    Talker::Queues.logger.bind(@exchange)\n  end\n  \n  after do\n    @logger.stop\n  end\n  \n  it \"should connect to database\" do\n    @logger.db.select \"SELECT 1 AS ONE;\" do |results|\n      results[0][\"ONE\"].should == \"1\"\n      done\n    end\n  end\n  \n  it \"should insert message\" do\n    message = { \"type\" => \"message\", \"user\" => {\"id\" => 1}, \"final\" => true, \"id\" => \"123\",\n                \"time\" => 5, \"content\" => \"ohaie\" }\n    \n    sql = \"INSERT INTO events (room_id, user_id, type, uuid, message, created_at) \" +\n          \"VALUES (1, 1, \'message\', \'123\', \'ohaie\', FROM_UNIXTIME(5))\"\n          \n    @logger.db.should_receive(:insert).with(sql, anything, anything)\n    \n    @exchange.publish encode(message), :exchange => \"talker.channel.1\"\n    done\n  end\n  \n  it \"should insert join\" do\n    message = { \"type\" => \"join\", \"user\" => {\"id\" => 1},\n                \"time\" => 5, \"content\" => \"ohaie\" }\n    \n    sql = \"INSERT INTO events (room_id, user_id, type, created_at) \" +\n          \"VALUES (1, 1, \'join\', FROM_UNIXTIME(5))\"\n    \n    @logger.db.should_receive(:insert).with(sql, anything, anything)\n    \n    @exchange.publish encode(message), :exchange => \"talker.channel.1\"\n    done\n  end\n  \n  it \"should insert leave\" do\n    message = { \"type\" => \"leave\", \"user\" => {\"id\" => 1},\n                \"time\" => 5, \"content\" => \"ohaie\" }\n    \n    sql = \"INSERT INTO events (room_id, user_id, type, created_at) \" +\n          \"VALUES (1, 1, \'leave\', FROM_UNIXTIME(5))\"\n    \n    @logger.db.should_receive(:insert).with(sql, anything, anything)\n    \n    @exchange.publish encode(message), :exchange => \"talker.channel.1\"\n    done\n  end\n  \n  it \"should insert paste\" do\n    message = { \"type\" => \"message\", \"user\" => {\"id\" => 1}, \"final\" => true, \"id\" => \"123\",\n                \"time\" => 5, \"content\" => \"ohaie...\", \n                \"paste\" => {\"id\" => \"abc123\", \"lines\" => 5, \"preview_lines\" => 3} }\n    \n    sql = \"INSERT INTO events (room_id, user_id, type, uuid, message, paste_permalink, created_at) \" +\n          \"VALUES (1, 1, \'message\', \'123\', \'ohaie...\', \'abc123\', FROM_UNIXTIME(5))\"\n    \n    @logger.db.should_receive(:insert).with(sql, anything, anything)\n    \n    @exchange.publish encode(message), :exchange => \"talker.channel.1\"\n    done\n  end\nend','9f45c3ae008a986b3ad0',NULL,'2009-10-15 18:50:34','2009-10-15 18:50:34'),(23,'require File.dirname(__FILE__) + \'/../test_helper\'\n\nclass RoomsControllerTest < ActionController::TestCase\n  def setup\n    @room = rooms(:main)\n    subdomain :master\n    login_as :quentin\n  end\n  \n  def test_index\n    get :index\n    assert_response :success\n    assert_not_nil assigns(:rooms)\n  end\n  \n  def test_show\n    get :show, :id => @room\n    assert_response :success\n    assert_equal assigns(:room), @room\n  end\n  \n  def test_new\n    get :new\n    assert_response :success\n    assert assigns(:room).new_record?\n  end\n  \n  def test_valid_create\n    post :create, :room => hash_for_room\n    assert_redirected_to room_path(assigns(:room))\n  end\n  \n  def test_invalid_create\n    Room.any_instance.stubs(:valid?).returns(false)\n    post :create, :room => hash_for_room\n    assert_response :success\n    assert_template \"new\"\n  end\nend','4fe428d278782fabee3e',NULL,'2009-10-15 19:07:18','2009-10-15 19:07:18'),(24,'$(function() {\n  $(\"#msgbox\")\n    .keydown(function(e) {\n      if (e.which == 13) {\n        if (this.value == \'\'){ return false }\n        ChatRoom.send(this.value, true);\n        ChatRoom.newMessage();\n        return false;\n      }\n    })\n    .keyup(function(e) {\n      if (e.which == 65) { // space\n        ChatRoom.send(this.value);\n      } else {\n        ChatRoom.sendLater(this.value);\n      }\n    })\n    .keydown(function(e){ // handle ESC and BACKSPACE cancelation of messages.\n      if (e.which == 27 || e.which == 8 && $(\'#msgbox\').val().length == 1){\n        ChatRoom.cancelMessage();\n      }\n    })\n    .keyup(function(e){\n      if (e.which == 9 && $(\'#msgbox\').val() == \'\'){\n        ChatRoom.cancelMessage();\n      }\n    });\n  \n  // reformat all messages loaded from db on first load\n  $(\'.content\').each(function(something, element){\n    element.innerHTML = ChatRoom.formatMessage(this.innerHTML, true);\n  });\n  \n  \n  ChatRoom.align();\n  ChatRoom.scrollToBottom();\n  ChatRoom.newMessage();\n  \n  $(window)\n    .blur(function(e){\n      ChatRoom.logMessages = true;\n    })\n    .focus(function(){\n      ChatRoom.logMessages = false;\n      ChatRoom.resetWindowTitle();\n    });\n  \n  $(window).keypress(function(e){\n    switch (e.which){\n      case 13: // enter\n        ChatRoom.align();\n        e.preventDefault();\n        break;\n      default:\n        // nothing at all.\n        break;\n    }\n  });\n});\n\nvar ChatRoom = {\n  messages: {},\n  currentMessage: null,\n  maxImageWidth: 400,\n  current_user: null,\n  \n  sendLater: function(data) {\n    if (data === \"\") return;\n    if (this.sendTimeout) clearTimeout(this.sendTimeout);\n    this.sendTimeout = setTimeout(function() {\n      ChatRoom.send(data);\n    }, 400);\n  },\n  \n  send: function(data, eol) {\n    if (data === \"\") return;\n    if (this.sendTimeout) clearTimeout(this.sendTimeout);\n    \n    var message = this.currentMessage;\