INSERT INTO `plugins` VALUES (1,'Hello Command','Sample plugin to greet the world and cure world hunger.','plugin.command = \'hello\';\nplugin.usage = \'/hello\';\n\nplugin.onCommand = function(event) {\n  if (event.command == \"hello\") {\n    alert(\'Hello world!\');\n    $(\'#msgbox\').val(\'\');\n    return false;\n  }\n}\n',1,NULL,1,'2009-11-16 19:23:15','2009-12-04 19:06:51'),(2,'Dock Badge','Dock icon warns you of what new messages you had while you were away.  Only works with Fluid and Prism.','plugin.count = 0;\n\nplugin.onLoaded = function() {\n  plugin.onBlur = function() {\n    plugin.onMessageReceived = function(event) {\n      plugin.count = plugin.count + 1;\n      window.dockBadge(plugin.count);\n    }\n  }\n\n  plugin.onFocus = function() {\n    plugin.count = 0;\n    window.dockBadge(\'\');\n    plugin.onMessageReceived = function(event) { }\n  }\n}\n',22,NULL,1,'2009-11-16 19:23:15','2009-11-16 19:23:15'),(3,'New Message Notifications','Provides Growl notifications of new messages when Talker is behind other windows.  Only works with Fluid and Prism.','plugin.onLoaded = function() {\n  plugin.onBlur = function() {\n    plugin.onMessageReceived = function(event) {\n      Talker.notify(event);\n    }\n  }\n\n  plugin.onFocus = function() {\n    plugin.onMessageReceived = function(event) { }\n  }\n}\n',22,NULL,1,'2009-11-16 19:23:15','2009-11-16 19:23:15'),(4,'Leave & Join Notifications','Provides Growl notifications of users leaving the room when Talker is behind other windows.  Only works with Fluid and Prism.','plugin.onLoaded = function() {\n  plugin.onBlur = function() {\n    plugin.onLeave = function(event) {\n      event.content = \"has left the room.\";\n      Talker.notify(event);\n    }\n    plugin.onJoin = function(event) {\n      event.content = \"has entered the room.\";\n      Talker.notify(event);\n    }\n  }\n\n  plugin.onFocus = function() {\n    plugin.onLeave = function() {  }\n    plugin.onJoin = function() {  }\n  }\n}\n',22,NULL,1,'2009-11-16 19:23:15','2009-11-16 19:23:15'),(5,'Title Message Count','Updates the title with message count when Talker is behind other windows.','plugin.count = 0;\nplugin.originalTitle = document.title;\n\nplugin.onLoaded = function() {\n  plugin.onBlur = function() {\n    plugin.onMessageReceived = function(event) {\n      plugin.count = plugin.count + 1;\n      document.title = \"(\" + plugin.count + \") \" + plugin.originalTitle;\n    }\n  }\n\n  plugin.onFocus = function() {\n    plugin.count = 0;\n    document.title = plugin.originalTitle;\n    plugin.onMessageReceived = function(event) { }\n  }\n}\n',22,NULL,1,'2009-11-16 19:23:15','2009-11-20 14:41:04'),(6,'Twitter Statuses','Converts twitter links to preview of twitter status.','plugin.onMessageInsertion = function(event){\n  var twitter_status_expression = /https*:\\/\\/twitter.com\\/\\w+\\/statuse?s?\\/(\\d+)/i;\n  var last_anchor = Talker.getLastInsertion().find(\'a\');\n\n  var last_href = last_anchor.attr(\'href\') || \'\';\n\n  if (twitter_status_expression.test(last_href)){\n    var id = last_href.match(twitter_status_expression)[1];\n    var url = \'http://twitter.com/statuses/show/\' + id + \'.json?callback=?\';\n    \n    if (last_anchor.hasClass(\'transformed\')){\n      return true; // Do not transform the link a second time.\n    }\n    \n    $.getJSON(url, function(data){\n      if (!last_anchor.hasClass(\'transformed\')){\n        last_anchor.html($(\'<q/>\').css({\n          clear: \'both\',\n          float: \'left\'\n        }).append(\n          $(\'<img/>\').attr({src: data.user.profile_image_url, height: 48, width: 48})\n          .css(\'margin\', \'4px\')\n        ).append(\'\"\' + data.text + \'\"\')).addClass(\'transformed\'); // tag as tranformed so we don\'t do it again\n      }\n    });\n  }\n}\n',22,NULL,1,'2009-11-16 19:23:15','2009-11-26 15:09:58'),(7,'Leave & Join','Shows who leaves and joins the room in the log.','plugin.onLeave = function(event) {\nTalker.insertNotice(event, h(event.user.name) + \' has left the room\');\n}\nplugin.onJoin = function(event) {\nTalker.insertNotice(event, h(event.user.name) + \' has entered the room\');\n}\n',22,NULL,1,'2009-11-16 19:23:15','2009-11-17 13:43:35'),(8,'Emoticons Formatter','Shows common emoticons.','plugin.Emoticon = function(matcher, path, meaning){\n  this.matcher = matcher;\n\n  this.replacementString = [\n    \' <img src=\"\', path, \n    \'\" class=\"emoticons\" height=\"16\" width=\"16\" style=\"position:relative; top:2px\" alt=\"\', meaning, \n    \'\" title=\"\', meaning, \'\" /> \'\n  ].join(\'\');\n}\n\nplugin.emoticons = [\n  new plugin.Emoticon(/(^|\\s)>:-?\\(($|\\s)/,       \"/images/icons/smiley-evil.png\",      \"evil\"),\n  new plugin.Emoticon(/(^|\\s):-?\\)\\)($|\\s)/,      \"/images/icons/smiley-lol.png\",       \"laughing\"),\n  new plugin.Emoticon(/(^|\\s):-?\\)($|\\s)/,        \"/images/icons/smiley.png\",           \"smiling\"),\n  new plugin.Emoticon(/(^|\\s):-?D($|\\s)/,         \"/images/icons/smiley-grin.png\",      \"grin\"),\n  new plugin.Emoticon(/(^|\\s)X-?\\(($|\\s)/,        \"/images/icons/smiley-mad.png\",       \"angry\"),\n  new plugin.Emoticon(/(^|\\s):-?\\(($|\\s)/,        \"/images/icons/smiley-sad.png\",       \"sad\"),\n  new plugin.Emoticon(/(^|\\s);-?\\(($|\\s)/,        \"/images/icons/smiley-cry.png\",       \"cry\"),\n  new plugin.Emoticon(/(^|\\s)[B|8]-?\\)($|\\s)/,    \"/images/icons/smiley-cool.png\",      \"cool\"),\n  new plugin.Emoticon(/(^|\\s):-?S($|\\s)/,         \"/images/icons/smiley-confuse.png\",   \"confused\"),\n  new plugin.Emoticon(/(^|\\s):-?O($|\\s)/,         \"/images/icons/smiley-eek.png\",       \"shocked\"),\n  new plugin.Emoticon(/(^|\\s):-?P($|\\s)/i,        \"/images/icons/smiley-razz.png\",      \"razz\")\n];\n\nplugin.onMessageInsertion = function(event) {\n  var element = Talker.getLastInsertion();\n\n  _.each(plugin.emoticons, function(emoticon) {\n    element.replace(emoticon.matcher, emoticon.replacementString);\n  });\n}\n',22,NULL,1,'2009-11-16 19:23:15','2009-12-14 15:11:06'),(34,'Test','This is an awesome test','console.info(\"it works\")',1,1,0,'2009-11-18 13:49:43','2009-11-18 13:49:43'),(35,'Textile','',' /*\r\n * This is the orginial function from Stuart Langridge at http://www.kryogenix.org/\r\n * This is the update function from Jeff Minard - http://www.jrm.cc/\r\n */\r\nplugin.processTextile = function(s) {\r\n  var r = s;\r\n  // quick tags first\r\n  qtags = [[\'\\\\*\', \'strong\'],\r\n  [\'\\\\?\\\\?\', \'cite\'],\r\n  [\'\\\\+\', \'ins\'],  //fixed\r\n  [\'~\', \'sub\'],   \r\n  [\'\\\\^\', \'sup\'], // me\r\n  [\'@\', \'code\']];\r\n  for (var i=0;i<qtags.length;i++) {\r\n    ttag = qtags[i][0]; htag = qtags[i][1];\r\n    re = new RegExp(ttag+\'\\\\b(.+?)\\\\b\'+ttag,\'g\');\r\n    r = r.replace(re,\'<\'+htag+\'>\'+\'$1\'+\'</\'+htag+\'>\');\r\n  }\r\n  // underscores count as part of a word, so do them separately\r\n  re = new RegExp(\'\\\\b_(.+?)_\\\\b\',\'g\');\r\n  r = r.replace(re,\'<em>$1</em>\');\r\n\r\n  //jeff: so do dashes\r\n  re = new RegExp(\'[\\s\\n]-(.+?)-[\\s\\n]\',\'g\');\r\n  r = r.replace(re,\'<del>$1</del>\');\r\n\r\n  // links\r\n  re = new RegExp(\'\"\\\\b(.+?)\\\\(\\\\b(.+?)\\\\b\\\\)\":([^\\\\s]+)\',\'g\');\r\n  r = r.replace(re,\'<a href=\"$3\" title=\"$2\">$1</a>\');\r\n  re = new RegExp(\'\"\\\\b(.+?)\\\\b\":([^\\\\s]+)\',\'g\');\r\n  r = r.replace(re,\'<a href=\"$2\">$1</a>\');\r\n\r\n  // images\r\n  re = new RegExp(\'!\\\\b(.+?)\\\\(\\\\b(.+?)\\\\b\\\\)!\',\'g\');\r\n  r = r.replace(re,\'<img src=\"$1\" alt=\"$2\">\');\r\n  re = new RegExp(\'!\\\\b(.+?)\\\\b!\',\'g\');\r\n  r = r.replace(re,\'<img src=\"$1\">\');\r\n\r\n  // block level formatting\r\n\r\n  // Jeff\'s hack to show single line breaks as they should.\r\n  // insert breaks - but you get some....stupid ones\r\n  re = new RegExp(\'(.*)\\n([^#\\*\\n].*)\',\'g\');\r\n  r = r.replace(re,\'$1<br />$2\');\r\n  // remove the stupid breaks.\r\n  re = new RegExp(\'\\n<br />\',\'g\');\r\n  r = r.replace(re,\'\\n\');\r\n\r\n  lines = r.split(\'\\n\');\r\n  nr = \'\';\r\n  for (var i=0;i<lines.length;i++) {\r\n    line = lines[i].replace(/\\s*$/,\'\');\r\n    changed = 0;\r\n    if (line.search(/^\\s*bq\\.\\s+/) != -1) { \r\n      line = line.replace(/^\\s*bq\\.\\s+/,\'\\t<blockquote>\')+\'</blockquote>\'; \r\n      changed = 1; \r\n    }\r\n\r\n    // jeff adds h#.\r\n    if (line.search(/^\\s*h[1|2|3|4|5|6]\\.\\s+/) != -1) { \r\n      re = new RegExp(\'h([1|2|3|4|5|6])\\.(.+)\',\'g\');\r\n      line = line.replace(re,\'<h$1>$2</h$1>\');\r\n      changed = 1; \r\n    }\r\n\r\n    if (line.search(/^\\s*\\*\\s+/) != -1) { line = line.replace(/^\\s*\\*\\s+/,\'\\t<liu>\') + \'</liu>\'; changed = 1; } // * for bullet list; make up an liu tag to be fixed later\r\n    if (line.search(/^\\s*#\\s+/) != -1) { line = line.replace(/^\\s*#\\s+/,\'\\t<lio>\') + \'</lio>\'; changed = 1; } // # for numeric list; make up an lio tag to be fixed later\r\n    lines[i] = line + \'\\n\';\r\n  }\r\n\r\n  // Second pass to do lists\r\n  inlist = 0; \r\n  listtype = \'\';\r\n  for (var i=0;i<lines.length;i++) {\r\n    line = lines[i];\r\n    if (inlist && listtype == \'ul\' && !line.match(/^\\t<liu/)) { line = \'</ul>\\n\' + line; inlist = 0; }\r\n    if (inlist && listtype == \'ol\' && !line.match(/^\\t<lio/)) { line = \'</ol>\\n\' + line; inlist = 0; }\r\n    if (!inlist && line.match(/^\\t<liu/)) { line = \'<ul>\' + line; inlist = 1; listtype = \'ul\'; }\r\n    if (!inlist && line.match(/^\\t<lio/)) { line = \'<ol>\' + line; inlist = 1; listtype = \'ol\'; }\r\n    lines[i] = line;\r\n  }\r\n\r\n  r = lines.join(\'\\n\');\r\n  // jeff added : will correctly replace <li(o|u)> AND </li(o|u)>\r\n  r = r.replace(/li[o|u]>/g,\'li>\');\r\n\r\n  return r;\r\n}\r\n\r\nplugin.onMessageReceived = function(event) {\r\n  if (!Talker.isPaste(event)) {\r\n    console.info(\"\'\" + plugin.processTextile(event.content) + \"\'\");\r\n    Talker.insertMessage(event, plugin.proc